mapper.insert:
insert into communication (id, content, img_urls, like_num,
  see_num, submit_user_id, nickname, 
  avatar_url, submit_time, is_show
  )
values (#{id,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, #{imgUrls,jdbcType=VARCHAR},
  #{likeNum,jdbcType=INTEGER},
  #{seeNum,jdbcType=INTEGER}, #{submitUserId,jdbcType=INTEGER}, #{nickname,jdbcType=VARCHAR}, 
  #{avatarUrl,jdbcType=VARCHAR}, #{msgSubmitTime,jdbcType=TIMESTAMP}, #{isShow,jdbcType=INTEGER}
  )

mapper.select:
select * from communication where 1 = 1
and submit_user_id = #{userId}
and is_show = 1 order by submit_time desc
